import { useState, useEffect, useMemo } from "react";
import { AiOutlineArrowRight } from "react-icons/ai";
import { instructionsData } from "../../utils/instructionsData";
import { InfoToolTip } from "../../components/toolTip/InfoToolTip";
import cx from "classnames";
import styles from "./InitialScreen.module.css";
import appStyles from "../../App.module.css";

export const InitialScreen = ({ setStartQuiz, isRetakeQuiz }) => {
  const textToDisplay = useMemo(
    () => [
      <div key="1" className={styles.countText}>
        <p>1</p>
        <p>Ready</p>
      </div>,
      <div key="2" className={styles.countText}>
        <p>2</p>
        <p>Set</p>
      </div>,
      <div key="3" className={styles.countText}>
        <p>3</p>
        <p>Quiz</p>
      </div>
    ],
    []
  );

  const [click, setClicked] = useState(false);
  const [displayText, setDisplayText] = useState(textToDisplay[0]);
  const [count, setCount] = useState(1);
  const [restartTimer, setRestartTimer] = useState(false);

  useEffect(() => {
    if (click || restartTimer) {
      const interval = setInterval(() => {
        setDisplayText(textToDisplay[count]);
        setCount((prevCount) => prevCount + 1);
      }, 1000);

      setTimeout(() => {
        clearInterval(interval);
        setStartQuiz(true);
        setCount(1);
        setRestartTimer(false);
      }, 3000);

      return () => clearInterval(interval);
    }
  }, [count, textToDisplay, setStartQuiz, click, restartTimer]);

  useEffect(() => {
    if (isRetakeQuiz) {
      setClicked(true);
      setDisplayText(textToDisplay[0]);
      setCount(1);
      setRestartTimer(true);
    }
  }, [isRetakeQuiz, textToDisplay]);

  return (
    <div className={cx(styles.wrapper, click && appStyles.fadeInRight)}>
      {!click ? (
        <>
          <div className={cx(styles.left, appStyles.fadeInBottom)}>
            <p>takeU</p>
          </div>
          <div className={cx(styles.right, appStyles.fadeInTop)}>
            <p>backward</p>
          </div>
          <div className={styles.buttonWrapper}>
            <InfoToolTip
              text="Click to start the Quiz!"
              component={
                <button onClick={() => setClicked(true)} className={appStyles.fadeInLeft}>
                  <span>Enter Quiz Arena</span>
                  <AiOutlineArrowRight />
                </button>
              }
            />
          </div>
        </>
      ) : (
        <>
          <div className={cx(styles.counter, appStyles.pulse)}>
            {displayText}
          </div>
          <div className={styles.instructions}>
            <h3>{instructionsData?.title}:</h3>
            <ol>
              {instructionsData?.data?.map((instruction, index) => (
                <li key={index}>{instruction}</li>
              ))}
            </ol>
          </div>
        </>
      )}
    </div>
  );
};
