import styles from "./Question.module.css";
import { TiTickOutline, TiTimesOutline } from "react-icons/ti";

export const Question = ({ index, data, userAnswer, isCorrect }) => {
  return (
    <div key={index} className={styles.questionDiv}>
      <p className={styles.question}>
        {index + 1}. {data.question}
      </p>
      <ul className={styles.options}>
        {data.choices.map((option, idx) => {
          const isSelected = userAnswer === option;
          const optionStyle = isSelected
            ? isCorrect
              ? styles.correctOption
              : styles.incorrectOption
            : styles.option;

          return (
            <li key={idx} className={optionStyle}>
              {option} &nbsp;
              {option === data.correctAnswer && (
                <TiTickOutline style={{ fontSize: "20px", color: "green" }} />
              )}
              {isSelected && !isCorrect && (
                <TiTimesOutline style={{ fontSize: "20px", color: "red" }} />
              )}
            </li>
          );
        })}
      </ul>
      {userAnswer && (
        <div className={styles.answerStatus}>
          {isCorrect ? (
            <span className={styles.correctText}>Correct</span>
          ) : (
            <span className={styles.incorrectText}>Incorrect</span>
          )}
        </div>
      )}
    </div>
  );
};
