import { useState, useEffect, useMemo } from "react";
import styles from "./InitialScreen.module.css";
import { AiOutlineArrowRight } from "react-icons/ai";

export const InitialScreen = ({ setStartQuiz, isRetakeQuiz }) => {
  const textToDisplay = useMemo(
    () => [
      <div key="1">
        <p className={styles.numberStyling}>1</p>
        <p className={styles.textStyling}>Ready</p>
      </div>,
      <div key="2">
        <p className={styles.numberStyling}>2</p>
        <p className={styles.textStyling}>Set</p>
      </div>,
      <div key="3">
        <p className={styles.numberStyling}>3</p>
        <p className={styles.textStyling}>Quiz</p>
      </div>,
    ],
    []
  );

  const [click, setClicked] = useState(false);
  const [displayText, setDisplayText] = useState(textToDisplay[0]);
  const [count, setCount] = useState(1);

  useEffect(() => {
    if (click) {
      const interval = setInterval(() => {
        setDisplayText(textToDisplay[count]);
        setCount((prevCount) => prevCount + 1);
      }, 1000);

      setTimeout(() => {
        clearInterval(interval);
        setStartQuiz(true);
        setCount(1);
      }, 3000);

      return () => clearInterval(interval);
    }
  }, [count, textToDisplay, setStartQuiz, click]);

  useEffect(() => {
    if (isRetakeQuiz) {
      setClicked(true);
      setDisplayText(textToDisplay[0]);
      setCount(1);
    }
  }, [isRetakeQuiz, textToDisplay]);

  return (
    <div className={`${styles.initialScreenWrapper}`}>
      {!click ? (
        <div className={styles.contentWrapper}>
          <h1 className={styles.title}>Connect with our community</h1>
          <div className={styles.stats}>
            <div className={styles.statBox}>
              <p className={styles.statNumber}>530K+</p>
              <p className={styles.statPlatform}>YouTube</p>
            </div>
            <div className={styles.statBox}>
              <p className={styles.statNumber}>120K+</p>
              <p className={styles.statPlatform}>Twitter</p>
            </div>
            <div className={styles.statBox}>
              <p className={styles.statNumber}>120K+</p>
              <p className={styles.statPlatform}>Instagram</p>
            </div>
            <div className={styles.statBox}>
              <p className={styles.statNumber}>560K+</p>
              <p className={styles.statPlatform}>LinkedIn</p>
            </div>
          </div>
          <button
            onClick={() => setClicked(true)}
            className={styles.startButton}
          >
            <span>Enter the Quiz Arena!</span>
            <AiOutlineArrowRight />
          </button>
        </div>
      ) : (
        <div className={styles.counterWrapper}>
          {displayText}
        </div>
      )}
    </div>
  );
};
