import styles from "./Question.module.css";

export const Question = ({ index, data, userAnswer, isCorrect }) => {
  return (
    <div className={styles.questionWrapper}>
      <h3>{`${index + 1}. ${data.question}`}</h3>
      <div className={styles.options}>
        {data.choices.map((choice, i) => (
          <div
            key={i}
            className={userAnswer === choice ? (isCorrect ? styles.correctAnswer : styles.incorrectAnswer) : styles.option}
          >
            {choice}
          </div>
        ))}
      </div>
      <div className={styles.answerStatus}>
        {userAnswer && (
          <span className={isCorrect ? styles.correctText : styles.incorrectText}>
            {isCorrect ? "Correct" : "Incorrect"} (Your answer: {userAnswer})
          </span>
        )}
      </div>
    </div>
  );
};
