import styles from "./Question.module.css";
import { TiTickOutline } from "react-icons/ti";

export const Question = ({ index, data, selectedAnswer, onAnswerSelected }) => {
  const { question, choices, correctAnswer } = data;

  return (
    <div key={index} className={styles.questionDiv}>
      <p className={styles.question}>
        {index + 1}. {question}
      </p>
      <ul className={styles.options}>
        {choices.map((option, idx) => {
          // Check if the option is correct
          const isCorrect = option === correctAnswer;
          const isSelected = selectedAnswer === option;

          // Determine the class to apply based on whether the answer is correct or incorrect
          const optionClass = isSelected
            ? isCorrect
              ? styles.correctOption
              : styles.incorrectOption
            : styles.option;

          return (
            <li
              key={idx}
              className={optionClass}
              onClick={() => onAnswerSelected(index, option)} // Update selected answer
            >
              {option} &nbsp;
              {isCorrect && (
                <TiTickOutline
                  style={{
                    fontSize: "20px",
                    color: "green",
                  }}
                />
              )}
            </li>
          );
        })}
      </ul>
    </div>
  );
};
